import React from 'react';
// ¡CORRECCIÓN DE RUTA AQUÍ!
// La ruta ahora es '../assets/imagen_portada.jpg' porque HeroSection.js
// se encuentra en 'src/components/' y la imagen en 'src/assets/'.
// Necesitamos subir un nivel (..) y luego entrar en la carpeta 'assets'.
import imagenPortada from '../assets/imagen_portada.jpg';

function HeroSection() {
  return (
    <section
      // Clases de Tailwind para la sección de cabecera.
      // - relative: Permite posicionar elementos hijos de forma absoluta (como el overlay).
      // - bg-gradient-to-r from-blue-700 to-indigo-800: Degardado de color de fondo.
      // - text-white: Color del texto.
      // - py-20 md:py-32: Padding vertical responsivo.
      // - text-center: Centra el texto.
      // - shadow-lg: Sombra grande.
      //
      // Clases para la imagen de fondo:
      // - bg-cover: Asegura que la imagen de fondo cubra toda la sección.
      // - bg-center: Centra la imagen de fondo.
      // - bg-no-repeat: Evita que la imagen de fondo se repita.
      className="relative bg-gradient-to-r from-blue-700 to-indigo-800 text-white py-20 md:py-32 text-center shadow-lg
                 bg-cover bg-center bg-no-repeat"
      // Estilo inline para la imagen de fondo.
      // Usamos 'url()' con la variable 'imagenPortada' que ya ha sido procesada por Webpack.
      style={{ backgroundImage: `url(${imagenPortada})` }}
    >
      {/*
        Este 'div' crea una capa semitransparente sobre la imagen de fondo.
        Es esencial para mejorar la legibilidad del texto blanco sobre la imagen.
        - absolute inset-0: Posiciona este div para que ocupe el 100% del ancho y alto de la sección.
        - bg-black: Color de la capa (negro).
        - opacity-50: Nivel de transparencia (50%). Puedes ajustarlo si lo deseas más claro o más oscuro.
      */}
      <div className="absolute inset-0 bg-black opacity-50"></div>

      {/*
        Este 'div' contiene todo el contenido principal de la cabecera (foto, títulos, botón).
        - container mx-auto px-6: Clases para centrar el contenido y añadir padding horizontal.
        - relative z-10: **Muy Importante**. Asegura que este contenido se muestre POR ENCIMA
          del overlay y la imagen de fondo.
      */}
      <div className="container mx-auto px-6 relative z-10">
        {/*
          Etiqueta 'img' para la foto de perfil.
          Utilizamos la misma imagen importada, 'imagenPortada', para la foto de perfil.
          - src={imagenPortada}: La fuente de la imagen es la variable que contiene la URL final.
          - alt="Miguel Mota": Texto alternativo para accesibilidad.
          - className: Clases de Tailwind para el estilo circular, tamaño y borde de la imagen.
        */}
        <img
          src={imagenPortada}
          alt="Miguel Mota"
          className="w-32 h-32 rounded-full mx-auto mb-6 border-4 border-white shadow-xl"
        />

        {/* Título principal de la cabecera */}
        <h1 className="text-4xl md:text-6xl font-extrabold leading-tight mb-4 animate-fade-in-down">
          Miguel Mota
        </h1>
        {/* Subtítulo o descripción profesional */}
        <p className="text-xl md:text-3xl font-light mb-8 opacity-90 animate-fade-in-up">
          Experto en Desarrollo de Software y Liderazgo de Equipos
        </p>
        {/* Botón de llamada a la acción */}
        <a
          href="#contact" // Asegúrate de que este enlace dirija a tu sección de contacto o a tu email.
          className="bg-white text-blue-700 hover:bg-blue-100 font-bold py-3 px-8 rounded-full text-lg shadow-xl transition duration-300 ease-in-out transform hover:scale-105"
        >
          Contactar
        </a>
      </div>
    </section>
  );
}

export default HeroSection;
