import React from 'react';
// Importamos la imagen que servirá como fondo y como imagen de perfil.
// Es crucial que esta ruta sea correcta. Si 'imagen_portada.jpg' está en 'src/assets/',
// entonces './assets/imagen_portada.jpg' es la ruta relativa correcta desde HeroSection.js.
import imagenPortada from './assets/imagen_portada.jpg';

function HeroSection() {
  return (
    <section
      // Clases para el fondo de la sección:
      // - relative: Necesario para que el 'div' del overlay se posicione correctamente.
      // - bg-cover, bg-center, bg-no-repeat: Controlan cómo se muestra la imagen de fondo.
      // - Las propiedades de color degradado y sombra se mantienen.
      className="relative bg-gradient-to-r from-blue-700 to-indigo-800 text-white py-20 md:py-32 text-center shadow-lg
                 bg-cover bg-center bg-no-repeat"
      // Utilizamos 'style' para aplicar la imagen de fondo directamente con la URL que Webpack nos proporciona al importar.
      style={{ backgroundImage: `url(${imagenPortada})` }}
    >
      {/*
        Este div crea una capa semitransparente sobre la imagen de fondo.
        Es fundamental para que el texto (blanco) sea legible sobre cualquier imagen.
        - absolute inset-0: Posiciona el div para que cubra toda la sección padre (HeroSection).
        - bg-black: Color de la capa (negro).
        - opacity-50: Transparencia de la capa (50%). Puedes ajustar este valor (ej., 30, 70).
      */}
      <div className="absolute inset-0 bg-black opacity-50"></div>

      {/*
        Este div contiene todo el contenido principal de la cabecera (foto, título, descripción, botón).
        - container mx-auto px-6: Clases de Tailwind para centrar el contenido y añadir padding horizontal.
        - relative z-10: Asegura que este contenido esté por encima del overlay y de la imagen de fondo.
      */}
      <div className="container mx-auto px-6 relative z-10">
        {/*
          Etiqueta img para la foto de perfil.
          Utilizamos la misma imagen importada, 'imagenPortada', para la foto de perfil.
          - src={imagenPortada}: La fuente de la imagen es la variable importada.
          - alt: Texto alternativo para accesibilidad.
          - className: Clases de Tailwind para el estilo de la imagen de perfil.
        */}
        <img
          src={imagenPortada}
          alt="Miguel Mota"
          className="w-32 h-32 rounded-full mx-auto mb-6 border-4 border-white shadow-xl"
        />

        {/* Título principal */}
        <h1 className="text-4xl md:text-6xl font-extrabold leading-tight mb-4 animate-fade-in-down">
          Miguel Mota
        </h1>
        {/* Descripción o subtítulo */}
        <p className="text-xl md:text-3xl font-light mb-8 opacity-90 animate-fade-in-up">
          Experto en Desarrollo de Software y Liderazgo de Equipos
        </p>
        {/* Botón de llamada a la acción */}
        <a
          href="#contact" // Cambia esto a la URL de tu sección de contacto o tu email
          className="bg-white text-blue-700 hover:bg-blue-100 font-bold py-3 px-8 rounded-full text-lg shadow-xl transition duration-300 ease-in-out transform hover:scale-105"
        >
          Contactar
        </a>
      </div>
    </section>
  );
}

export default HeroSection;
