import React from 'react';

function HeroSection() {
  return (
    <section
      className="relative bg-gradient-to-r from-blue-700 to-indigo-800 text-white py-20 md:py-32 text-center shadow-lg
                 bg-[url('/imagen-portada.jpg')] bg-cover bg-center bg-no-repeat" // <<-- AÑADE ESTAS CLASES
    >
      {/* Overlay para oscurecer la imagen y mejorar la legibilidad del texto */}
      <div className="absolute inset-0 bg-black opacity-50"></div> {/* <<-- AÑADE ESTO */}

      <div className="container mx-auto px-6 relative z-10"> {/* <<-- AÑADE relative z-10 */}
        {/* Foto de perfil */}
        <img
          src="/imagen-portada.jpg" // Asegúrate de que esta ruta sea correcta para la imagen de perfil
          alt="Miguel Mota"
          className="w-32 h-32 rounded-full mx-auto mb-6 border-4 border-white shadow-xl"
        />

        <h1 className="text-4xl md:text-6xl font-extrabold leading-tight mb-4 animate-fade-in-down">
          Miguel Mota
        </h1>
        <p className="text-xl md:text-3xl font-light mb-8 opacity-90 animate-fade-in-up">
          Experto en Desarrollo de Software y Liderazgo de Equipos
        </p>
        <a
          href="#contact"
          className="bg-white text-blue-700 hover:bg-blue-100 font-bold py-3 px-8 rounded-full text-lg shadow-xl transition duration-300 ease-in-out transform hover:scale-..."
        >
          Contactar
        </a>
      </div>
    </section>
  );
}

export default HeroSection;
